import React from 'react';

class UserRegistrationForm extends React.Component {
    constructor() {
        super();

        this.state = {
            firstName: "",
            lastName: "",
            email: "",
            number: "",
            username: "",
            password: "",
            confirmPassword: ""
        }

        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(e) {
        let name = e.target.name;

        if(name === "firstname") {
            this.setState({ firstName: e.target.value });
        } else if(name === "lastname") {
            this.setState({ lastName: e.target.value });
        } else if(name === "email") {
            this.setState({ email: e.target.value });
        } else if(name === "number") {
            this.setState({ number: e.target.value });
        } else if(name === "username") {
            this.setState({ username: e.target.value });
        } else if(name === "password") {
            this.setState({ password: e.target.value });
        } else if(name === "confirmPassword") {
            this.setState({ confirmPassword: e.target.value });
        }
    }

    handleSubmit(e) {
        console.log("HELLO");
        fetch("http://localhost:5000/registerUser", {
            credentials: 'same-origin',
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(this.state)
          }).then((response) => {
            if (!response.ok) throw Error(response.statusText);
            return response.json();
          }).catch((error) => console.log(error));
    }

    render() {
        const { firstName, lastName, email, number, username, password, confirmPassword } = this.state;

        return (
            <div>
                <form>
                    <label>
                        <p>First Name</p>
                        <input type="text" name="firstname" value={firstName} onChange={this.handleChange} />
                    </label>
                    <label>
                        <p>Last Name</p>
                        <input type="text" name="lastname" value={lastName} onChange={this.handleChange} />
                    </label>
                    <label>
                        <p>Email</p>
                        <input type="text" name="email" value={email} onChange={this.handleChange} />
                    </label>
                    <label>
                        <p>Phone Number</p>
                        <input type="text" name="number" value={number} onChange={this.handleChange} />
                    </label>
                    <label>
                        <p>Username</p>
                        <input type="text" name="username" value={username} onChange={this.handleChange} />
                    </label>
                    <label>
                        <p>Password</p>
                        <input type="text" name="password" value={password} onChange={this.handleChange} />
                    </label>
                    <label>
                        <p>Confirm Password</p>
                        <input type="text" name="confirmPassword" value={confirmPassword} onChange={this.handleChange} />
                    </label>

                    <button onSubmit={this.handleSubmit}>Submit</button>
                </form>
            </div>
        );
    }
}

export default UserRegistrationForm;
